import Recipe from './Recipe'
import './App.css';

const App = () => {

  const APP_ID = '3b276331';
  const APP_KEY = 'bae8af78b98ebdc942515c5e2ae6d26b';

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('chicken');

  useEffect(() =>{
    getRecipes()
    console.log("lets say we are fetting data")
  }, [])

  const getRecipes = async () =>{
    const response = await fetch(`https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`)
    const data = await response.json()
    setRecipes(data.hits)
    console.log(data.hits)
  }

  const updateSearch = (e) => {
    setSearch(e.target.value);
  }

  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
  }

  return (
    <div className="App">
      {/* <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css"></link> */}
      <h1>Recipe Finder</h1>
      <form className="search-form">
        {/* <div className="col-xs-4"> */}
          <input className="" type="text" value={search} onChange={updateSearch}/>
        {/* </div> */}
        {/* <div className="col-xs-2"> */}
          <button className="btn btn-primary-dark" type="submit"> Find Something! </button>
        {/* </div> */}
      </form>
      {recipes.map(recipe => (
          <Recipe
            key = {recipe.recipe.label}
            title = {recipe.recipe.label}
            calories = {recipe.recipe.calories}
            image = {recipe.recipe.image}
          />
      ))}
    </div>
  );
}

export default App;
