import React, {useEffect, useState } from "react";
import Recipe from './Recipe'
import './App.css';

const App = () => {

  const APP_ID = '3b276331';
  const APP_KEY = 'bae8af78b98ebdc942515c5e2ae6d26b';

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('chicken');

  const getRecipes = async () =>{
    const response = await fetch(`https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`)
    const data = await response.json()
    setRecipes(data.hits)
    console.log(data.hits)
  }

  useEffect(() =>{
    getRecipes()
  }, [query])


  const updateSearch = (e) => {
    setSearch(e.target.value);
    console.log(query)
  }

  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
  }

  return (
    <div className="App">
      {/* <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/css/bootstrap.min.css"></link> */}
      <div className="head">
      <h1>The Recipe Project</h1>
      <h4>by Satwikan</h4>
      </div>
      <form onSubmit={getSearch} className="search-form">
        <input className="search-bar" type="text" value={search} onChange={updateSearch}/>
        <button className="search-button btn btn-info" type="submit"> Find Something! </button>
      </form>
      <div className="recipes">
      {recipes.map(recipe => (
          <Recipe
            key = {recipe.recipe.label}
            title = {recipe.recipe.label}
            calories = {recipe.recipe.calories}
            image = {recipe.recipe.image}
            ingredients = {recipe.recipe.ingredients}
          />
      ))}
      <footer class="footer-dark">
        <div class="container">
            <div class="row">
                <div class="col-sm-6 col-md-3 item">
                    <h3>Services</h3>
                    <ul>
                        <li><a href="#">Web design</a></li>
                        <li><a href="#">Development</a></li>
                        <li><a href="#">Hosting</a></li>
                    </ul>
                </div>
                <div class="col-md-9 offset-md-0 item text">
                    <h3>About</h3>
                    <p>I am a CSE undergrad this is my project. A React App with React hooks and API.</p>
                </div>
            </div>
            <p class="copyright"><strong>Let&#39;s Chat:</strong></p>
            <div class="col-md-10 offset-md-1 item social"><a href="#"><i class="icon ion-social-facebook"></i></a><a href="#"><i class="icon ion-social-twitter"></i></a><a href="#"><i class="fab fa-github"></i></a><a href="#"><i class="fab fa-linkedin"></i></a></div>
        </div>
      </footer>
      <script src="assets/js/jquery-3.5.1.min.js"></script>
      <script src="assets/bootstap/js/bootstrap.min.js"></script>
      </div>
    </div>
  );
}

export default App;
